import { apiv2 } from '../src/index.js';
import dotenv from 'dotenv';
dotenv.config();

const client = new apiv2({
    clientId: process.env.CLIENT_ID,
    clientSecret: process.env.CLIENT_SECRET
});

const topPlays = await client.fetch('/users/2368744/scores/best');

console.log(topPlays.map(a => `${a.beatmapset.artist} - ${a.beatmapset.title} [${a.beatmap.version}] ${a.weight.pp}pp`));
